sysauto_script.script:
// Get today's time and date

var rightNow = new GlideDateTime();
// Query the database for NeedIt Task records with Due date field values within
// 24 hours from now.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var needitTasksGR=new GlideRecord('x_58872_needit_needit_task');
needitTasksGR.addQuery('due_date','>=',rightNow);
needitTasksGR.addQuery('due_date','<',gs.hoursAgo(-24));
needitTasksGR.addQuery('state', '!=',3);
needitTasksGR.query();
// Write a log message for each NeedIt Task Record due within 24 hours from now
while(needitTasksGR.next()){
	gs.info("This record will breach soon and need your attention" + needitTasksGR.number + ":" + needitTasksGR.due_date);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',needitTasksGR,needitTasksGR.number,gs.getUserName())
}
